import '../assets/css/style.css';
import TopNavbar from '../components/TopNavbar.vue';
import Footerbar from '../components/Footerbar.vue';
import SplitType from 'split-type';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import Lenis from '@studio-freight/lenis';
import LoginPopup from '../components/login.vue';

export default {

  components: { TopNavbar, Footerbar, LoginPopup },
  data() {
    return {
      showLoginPopup: false, // 控制是否顯示登入 popup
      targetRoute: '', // 記錄想要導航的路由
    };
  },
  name: "CemeterySection",
  mounted() {
    console.log(this.$router);
    // 其他初始化動畫和滾動邏輯
    const ghostText = new SplitType(".split_text");
    gsap.registerPlugin(ScrollTrigger);

    const tlGhost = gsap.timeline();
    tlGhost.from(".cemetery_text .char", {
      filter: "blur(20px)",
      stagger: 0.07,
      scrollTrigger: {
        trigger: ".cemetery_text",
        start: "top center",
        end: "bottom top+=300",
        scrub: 1.5,
      }
    });

    const lenis = new Lenis();
    function raf(time) {
      lenis.raf(time);
      requestAnimationFrame(raf);
    }
    requestAnimationFrame(raf);

    const envelope = document.querySelector(".envelope_wrapper");
    envelope.addEventListener("click", () => {
      envelope.classList.toggle("flap");
    });

    window.addEventListener("scroll", this.handleScroll);
  },

  beforeUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  },

  methods: {
    handleScroll() {
      let value = window.scrollY;
      this.$refs.cemeteryText.style.marginTop = `${value * 0.6}px`;
      this.$refs.treeLeft.style.transform = `translateX(-${value * 0.9}px)`;
      this.$refs.treeRight.style.transform = `translateX(${value * 0.9}px)`;
      this.$refs.gateLeft.style.transform = `translateX(-${value * 0.5}px)`;
      this.$refs.gateRight.style.transform = `translateX(${value * 0.5}px)`;
    },
    checkLogin(target) {
      // console.log("觸發checkLogin")
      const user = localStorage.getItem('user'); // 從 localStorage 檢查是否有登入
      // console.log("USER")
      if (user) {
        // console.log("login success")
        // 已登入，進行導航
        this.$router.push(target);
      } else {
        // 未登入，顯示登入 popup 並保存目標路由
        this.targetRoute = target;
        this.showLoginPopup = true;
      }
    },
    closePopup() {
      this.showLoginPopup = false;
    },
    handleLoginSuccess() {
      this.showLoginPopup = false;
      if (this.targetRoute) {
        // 登入後自動導航到之前的目標路由
        this.$router.push(this.targetRoute);
      }
    },
  
  },
};
</script>
